/**
 * @param  {[type]}  aurelia [description]
 * @return {[type]}          [description]
 */
export async function configure(aurelia) {
    aurelia.use
        .standardConfiguration();
    //.feature(PLATFORM.moduleName('./resources'));

    if (environment.debug) {
        aurelia.use.developmentLogging();
    }

    if (environment.testing) {
        aurelia.use.plugin(PLATFORM.moduleName('aurelia-testing'));
    }

    aurelia.use
        // .globalResources(['vc/lower', 'vc/upper', 'vc/price'])
        .plugin(PLATFORM.moduleName('aurelia-animator-css'))
        .plugin(PLATFORM.moduleName('aurelia-configuration'))
        .plugin(PLATFORM.moduleName('aurelia-validation'))
        .plugin(PLATFORM.moduleName('aurelia-api'), config => {
            /**
             * See {@link https://aurelia-api.spoonx.org/configuration.html}
             */
            config
            .registerEndpoint(
                'default',
                'https://jsonplaceholder.typicode.com/',
                {
                    credentials: 'same-origin',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            )
            .setDefaultEndpoint('api');
        })
        .plugin(PLATFORM.moduleName('aurelia-i18n'), (instance) => {
            // register backend plugin
            instance.i18next.use(Backend);
        });
    await aurelia.start();
    await aurelia.setRoot(PLATFORM.moduleName('app'));
}

import {inject} from 'aurelia-dependency-injection';
import {customAttribute} from 'aurelia-templating';
import {DOM} from 'aurelia-pal';

/**
 * `am-uuid` attribute used by `View` class to select an internal element generated by a component/template
 */
@customAttribute('am-uuid')
@inject(DOM.Element)
export class AmUuid {
    /**
     * Creates a new instance of Hide.
     * @param element Target element to conditionally hide. DOM.
     */
    constructor(element) {
        this.element = element;
    }
    /**
     *
     */
    bind() {
        this.element.setAttribute('am-uuid', this.value);
    }
    /**
     * Value Change Event
     * @param  {String}   newValue
     * @param  {String}   oldValue
     * @return {void}
     */
    valueChanged(newValue, oldValue) {
        this.element.style.backgroundColor = newValue;
    }
}

